import React, { Component } from 'react';
import ReactDOM from "react-dom";
import './MenuBar.css';

class MenuBar extends Component {

	render() {
		return (
			<div className="MenuBar">
				<MenuBarItem text="skills" id="item1"/>
				<MenuBarItem text="why ux?" id="item2"/>
				<MenuBarItem text="projects" id="item3"/>
				<MenuBarItem text="contact" id="item4"/>
				<MenuBarItem text="login" id="item5"/>
			</div>
		);
	}
}

class MenuBarFixed extends Component {
	constructor(props) {
		super(props);
		this.state = {
			fixed: true
		};
		this.handleScroll = this.handleScroll.bind(this);
	}

	componentDidMount() {
		this.node = ReactDOM.findDOMNode(this);
		window.addEventListener('scroll', this.handleScroll);
	}

	componentWillUnmount() {
		window.removeEventListener('scroll', this.handleScroll);
	}

	handleScroll() {
		const node = this.node;
    //this.setState({ fixed: (node.top > window.pageYOffset) });
		this.setState({ fixed: (window.pageYOffset > 350) });
  }

	render() {
		const style = {
			position: (this.state.fixed) ? 'fixed' : 'absolute',
			display: (this.state.fixed) ? 'flex' : 'none'
		};

		return (
			<div className="MenuBarFixed" style={style}>
				<MenuBarItem text="skills" id="item1"/>
				<MenuBarItem text="why ux?" id="item2"/>
				<MenuBarItem text="projects" id="item3"/>
				<MenuBarItem text="contact" id="item4"/>
				<MenuBarItem text="login" id="item5"/>
			</div>
		);
	}
}

class MenuBarItem extends Component {
	render() {
		return (
			<div className="MenuBarItem" id={this.props.id}>
				{this.props.text}
			</div>
		);
	}
}

export { MenuBar, MenuBarFixed, MenuBarItem };
