import React from 'react';
import './App.css';
import './lang.json';
import { Title, Subtitle, Text, CoverText, Signature, Iconic } from './components/Text/Text';
import { MenuBar, MenuBarItem } from './components/MenuBar/MenuBar';
import { Image, ImageRow } from './components/Image/Image';
import { ImageGallery } from './components/ImageGallery/ImageGallery';
import { Hexagon } from './components/Hexagon/Hexagon';
import ImageRowItem from './objects/ImageRowItem';

function App() {

	var lang = require('./lang.json');
	var host = window.location.href;
	var language = ((host.substr(host.lastIndexOf(".") + 1)) === 'dk') ? 'da' : 'en';
	var t;
	if (language === 'da') t = 
	console.log(lang["en"]);

	var trees = [
		new ImageRowItem("How the customer explained it", "tree_1"),
		new ImageRowItem("How the project leader understood it", "tree_2"),
		new ImageRowItem("How the developer programmed it", "tree_3"),
		new ImageRowItem("What the customer really needed", "tree_4")
	];

	return (
    <div className="App">
			<div id="cover" className="Section">
				<div position="center" className="Container">
					<Signature text="Kasper Jørgensen"/>
					<CoverText text="UX DESIGNER & DEVELOPER"/>
				</div>
				<div className="Shadow"></div>
			</div>
			<div id="intro" className="Section">
				<MenuBar>
					<MenuBarItem/>
				</MenuBar>
				<div position="paragraph" className="Container">
					<Subtitle text="My name is Kasper"/>
					<Text size="small" text="I am a danish UX designer, with a bachelor's degree in Interaction Design and I have always had the passion for creating great user experiences."/>
					<Text size="small" text="My motivation is the current lack of user insights and involvement, at today's companies, that often leads to misunderstandings and frustrated users."/>
					<Image id="me" name="me" format="png" width="300px"/>
				</div>
			</div>
			<div id="skills" className="Section">
				<div position="paragraph" className="Container">
					<Title text="skills"/>
					<Text text="My biggest strength is that I am like a swiss army knife when it comes to UX. I have a lot of experince in developing prototypes, designing wireframes, creating studies, analysing systems, articulating text... The list goes on and on."/>
				</div>
				<ImageGallery/>
			</div>
			<div id="ux" className="Section">
				<div position="paragraph" className="Container">
					<Title text="why ux?"/>
					<Text size="small" text="UX is not just about cool graphics and modern front-end design. It's about fully understanding your users and their needs, so that you are able to provide them with a satisfying experience. Equally important, you need a mutual understanding across the whole developer team, as misunderstandings often leads to bad user experiences."/>
					<ImageRow items={trees}/>
				</div>
			</div>
			<div id="projects" className="Section">
				<div position="paragraph" className="Container">
					<Title text="projects"/>
					<Text size="small" text="I have been working on a lot of exiting projects as a UX Designer. Some of them are from my time as a student at the university, and some of them are for huge companies, such as Nilfisk. They all gave me a lot of experience."/>
					<Hexagon></Hexagon>
				</div>
			</div>
			<div id="contact" className="Section">
				<div position="paragraph" className="Container">
					<Title text="contact"/>
					<Text text="I am always looking for new and exiting challenges, so if you are developing a product or service, and you want satisfied users, don’t hessitate to contact me."/>
					<Text text="Let’s dicuss how a good user experience can contribute to your project."/>
				</div>
			</div>
			<div id="footer" className="Section"></div>
    </div>
  );
}

export default App;
