import React, { Component } from 'react';
import './Image.css';

class Image extends Component {
	render() {
		const format = (this.props.format) ? `.${this.props.format}` : '.svg';
		const image = `./${this.props.name}${format}`;
		const style = { width: (this.props.width) ? this.props.width : 'auto' };
		return (
			<img style={style} className="Image" src={image} alt={this.props.name}>

			</img>
		);
	}
}

class ImageRow extends Component {
	render() {
		const items = this.props.items.map((d, i) => 
			<div className="ImageRowItem" key={i}>
				<div className="ImageRowItemDescription">{d.description}</div>
				<Image className="ImageRowItemImage" name={d.image} format="svg"/>
			</div>
		);
		return (
			<div className="ImageRow">
				{items}
			</div>
		);
	}
}

export { Image, ImageRow };
