import React, { Component } from 'react';
import './ImageGallery.css';
import Slider from 'react-slick';
import { Image } from '../Image/Image';

class ImageGallery extends Component {
	render() {
    var settings = {
      dots: false,
      infinite: true,
      speed: 500,
      slidesToShow: 1,
      slidesToScroll: 1
    };

		const items = this.props.items.map((d, i) => 
			<div className="ImageGalleryItem" key={i}>
				<Image className="ImageGalleryItemImage" name={d.image} format="png" width="120px"/>
				<div className="ImageRowGalleryDescription">{d.description}</div>
			</div>
		);

    return (
      <Slider {...settings}>
				{items}
      </Slider>
    );
  }
}

export { ImageGallery };
