import React, { Component } from 'react';
import './Text.css';

class Title extends Component {
	render() {
		const color = { color: (this.props.color) ? this.props.color : '#333333' };
		return (
			<div className="Title" style={color}>
				{this.props.text}
			</div>
		);
	}
}

class Subtitle extends Component {
	render() {
		const color = { color: (this.props.color) ? this.props.color : '#333333' };
		return (
			<div className="Subtitle">
				{this.props.text}
			</div>
		);
	}
}

class Text extends Component {
	render() {
		const color = { color: (this.props.color) ? this.props.color : '#333333' };
		return (
			<div className="Text" boxsize={this.props.size}>
				{this.props.text}
			</div>
		);
	}
}

class CoverText extends Component {
	render() {
		const color = { color: (this.props.color) ? this.props.color : '#333333' };
		return (
			<div className="CoverText">
				{this.props.text}
			</div>
		);
	}
}

class Signature extends Component {
	render() {
		const color = { color: (this.props.color) ? this.props.color : '#333333' };
		return (
			<div className="Signature">
				{this.props.text}
			</div>
		);
	}
}

class Iconic extends Component {
	render() {
		return (
			<div className="Iconic">

			</div>
		);
	}
}

export { Text, Title, Subtitle, CoverText, Signature, Iconic };
