import React, { Component } from 'react';
import './ImageGallery.css';
import Slider from 'react-slick';
import { Image } from '../Image/Image';

class ImageGallery extends Component {
	render() {
    var settings = {
			className: "ImageGallery",
      dots: false,
      infinite: true,
      speed: 500,
      slidesToShow: 2,
      slidesToScroll: 1,
			centerMode: true,
			centerPadding: '200px',
			focusOnSelect: true
    };

		const items = this.props.items.map((d, i) => 
			<div className="ImageGalleryItem" key={i}>
				<div className="ImageGalleryItemTitle">{d.title}</div>
				<Image className="ImageGalleryItemImage" name={d.image} format="png" width="500px"/>
				<div className="ImageGalleryItemDescription">{d.description}</div>
			</div>
		);

    return (
      <Slider {...settings}>
				{items}
      </Slider>
    );
  }
}

export { ImageGallery };
