import React, { Component } from 'react';
import ReactDOM from 'react-dom';
import './BackVideo.css';
import ReactPlayer from 'react-player'

class BackVideo extends Component {

	constructor(props) {
    super(props);
		this.state = { panning: 0 };
    this.handleScroll = this.handleScroll.bind(this);
  }

	componentDidMount() {
    window.addEventListener('scroll', this.handleScroll);
		this.node = ReactDOM.findDOMNode(this);
  }

  componentWillUnmount() {
    window.removeEventListener('scroll', this.handleScroll);
  }

	handleScroll() {
		var node = this.node.getBoundingClientRect();
		if (node.top < window.innerHeight && node.top > (node.height) * -1) {
			this.setState({ panning: node.top });
		}
	}

	render() {
		return (
			<div className="BackVideo">
				<ReactPlayer
					style={{marginTop: this.state.panning}}
          className='react-player'
          url='/video/universe.mp4'
          width='100%'
					height='auto'
					playing
					muted
        />
			</div>
		);
	}
}

export { BackVideo };
